mapper.select:
select company_id as CompanyId, business_id as businessId FROM tb_basic_business WHERE 1=1
AND company_id IN
#{companyId}

select sum(fee+fee2+fee3) from (
/* 线上出款业务一次性收取服务费*/ select a.business_id,ifnull(sum(b.sub_company_charge),0) `fee`, (ifnull(sum(b.guarantee_amount),0)+ifnull(sum(b.tuandai_amount),0)+ +ifnull(sum(b.freed_amount),0)+ifnull(sum(b.agency_amount),0)) `fee2`, ifnull(sum(b.cooperative_td_com_amount),0) `fee3`
from tb_basic_business a join tb_tuandai_project_info b on a.business_id = b.business_id left join tb_renewal_business c on a.business_id = c.renewal_business_id where a.business_id = #{original_business_id,jdbcType=VARCHAR} and a.output_platform_id=1 and c.renewal_business_id is null and b.begin_time is not null group by a.business_id union all
/*线下出款业务一次性收取服务费*/ select t1.orig_business_id,sum(t3.plan_amount) `fee`, 0 as 'fee2',0 as 'fee3' FROM tb_repayment_biz_plan_list t1 join tb_repayment_biz_plan t2 on t1.plan_id = t2.plan_id join tb_repayment_biz_plan_list_detail t3 on t1.plan_list_id= t3.plan_list_id join tb_basic_business t4 on t1.orig_business_id = t4.business_id WHERE t1.orig_business_id = #{original_business_id,jdbcType=VARCHAR} AND IFNULL(t3.plan_item_type, 0) = 30 AND IFNULL(t1.repay_flag, 0) != 6 AND ifnull(t1.period,0) =0 AND t2.is_defer=0 AND ifnull(t4.output_platform_id,0) = 0 group by t1.orig_business_id ) tab group by tab.business_id

SELECT (@rowNum:=@rowNum+1) as rowNo,c_log.collection_user AS liquidationOneId,user.user_name AS liquidationOneName,c_log.`describe` as `describe`, c_status.set_Way AS setWay,c_log.create_time AS setTime FROM tb_collection_status c_status LEFT JOIN tb_collection_log c_log ON c_status.crp_id=c_log.crp_id LEFT JOIN tb_sys_user user ON user.user_id=c_log.collection_user, (Select (@rowNum :=0) ) b
where c_status.collection_status=1 and c_status.phone_staff is not null
and c_status.crp_id=#{crpId,jdbcType=VARCHAR}

SELECT (@rowNum:=@rowNum+1) as rowNo,c_log.collection_user AS liquidationTwoId,user.user_name AS liquidationTwoName,c_log.`describe` as `describe`, c_status.set_Way AS setWay,c_log.create_time AS setTime FROM tb_collection_status c_status LEFT JOIN tb_collection_log c_log ON c_status.crp_id=c_log.crp_id LEFT JOIN tb_sys_user user ON user.user_id=c_log.collection_user, (Select (@rowNum :=0) ) b
where c_status.collection_status=50 and c_status.visit_staff is not null
and c_status.crp_id=#{crpId,jdbcType=VARCHAR}
