mapper.select:
SELECT trb.renewal_business_id AS id, trb.original_business_id , tbb.customer_name AS customer, tbb.original_name AS salesman, tbb.borrow_money AS loanAmount, CONCAT( tbb.borrow_limit, ( IF ( tbb.borrow_limit_unit = "1", "个月", "天" ) ) ) AS loanTerm, tbbt.business_type_name AS businessType, trb.renewal_business_id AS extensionId, trb.current_process_name AS STATUS, CONCAT( tbba.last_review_limit, ( IF ( tbba.last_review_limit_unit = "1", "个月", "天" ) ) ) AS extensionTerm, IFNULL( tbba.last_review_money, 0 ) AS extensionAmount, (select sum(IFNULL(fact_amount,0)) FROM tb_repayment_biz_plan_list_detail WHERE business_id = trb.original_business_id and plan_item_type = 10 ) as repayAmount FROM tb_renewal_business trb LEFT JOIN tb_basic_business tbb ON tbb.business_id = trb.original_business_id LEFT JOIN tb_basic_business_approve tbba ON tbba.business_id = tbb.business_id left join tb_basic_business_type tbbt on tbbt.business_type_id = tbba.apply_repayment_type
1=1
AND trb.create_time >= #{loanExtReq.dateStart}
AND trb.create_time <= #{loanExtReq.dateEnd}
AND tbb.company_id = #{loanExtReq.deptId}
AND ( trb.original_business_id like '%${loanExtReq.businessId}%' )
AND tbb.original_name like '%${loanExtReq.salesman}%'
AND tbb.customer_name like '%${loanExtReq.customer}%'
AND tbba.apply_repayment_type = #{loanExtReq.businessType}
AND trb.renewal_business_id like '%${loanExtReq.extensionId}%'
ORDER BY trb.create_time desc LIMIT ${(loanExtReq.page-1) * loanExtReq.limit}, #{loanExtReq.limit,jdbcType=DECIMAL}

SELECT count(1) FROM tb_renewal_business trb LEFT JOIN tb_basic_business tbb ON tbb.business_id = trb.original_business_id LEFT JOIN tb_basic_business_approve tbba ON tbba.business_id = tbb.business_id left join tb_basic_business_type tbbt on tbbt.business_type_id = tbba.apply_repayment_type
1=1
AND trb.create_time >= #{loanExtReq.dateStart}
AND trb.create_time <= #{loanExtReq.dateEnd}
AND tbb.company_id = #{loanExtReq.deptId}
AND ( trb.original_business_id like '%${loanExtReq.businessId}%' )
AND tbb.original_name like '%${loanExtReq.salesman}%'
AND tbb.customer_name like '%${loanExtReq.customer}%'
AND tbba.apply_repayment_type = #{loanExtReq.businessType}
AND trb.renewal_business_id like '%${loanExtReq.extensionId}%'
