mapper.select:
SELECT SUM(t3.proj_plan_amount) - SUM(t3.derate_amount) - SUM(IFNULL(t3.proj_fact_amount, 0.00)) FROM tb_repayment_proj_plan t1 INNER JOIN tb_repayment_proj_plan_list t2 ON t1.proj_plan_id = t2.proj_plan_id INNER JOIN tb_repayment_proj_plan_list_detail t3 ON t2.proj_plan_list_id = t3.proj_plan_list_id WHERE 1 = 1
AND t1.business_id =#{businessId}
AND t1.plan_id =#{planId}

SELECT t3.proj_plan_list_id AS projPlanListId, t3.proj_plan_id AS projPlanId, t3.business_id AS businessId, t3.plan_id AS planId, t3.after_id AS afterId, t3.plan_list_id AS planListId, t1.project_id AS projectId, t1.original_business_id AS origBusinessId, t4.period AS period, IFNULL(t3.repay_status,0) AS repayStatus, t4.proj_plan_detail_id AS projPlanDetailId, t4.plan_detail_id AS planDetailId, t4.share_profit_index AS shareProfitIndex, t4.plan_item_type AS planItemType, t4.plan_item_name AS planItemName, t4.fee_id AS feeId, t4.proj_plan_amount AS projPlanAmount, t4.derate_amount AS derateAmount, t4.proj_fact_amount AS projFactAmount, (t4.proj_plan_amount-t4.derate_amount-IFNULL(t4.proj_fact_amount,0.00)) AS money FROM tb_repayment_proj_plan t1 INNER JOIN tb_repayment_proj_plan_list t3 ON t1.proj_plan_id=t3.proj_plan_id INNER JOIN tb_repayment_proj_plan_list_detail t4 ON t3.proj_plan_list_id = t4.proj_plan_list_id WHERE 1=1
AND t1.business_id = #{businessId}
AND t1.plan_id = #{planId}
ORDER BY t3.due_date,t4.plan_item_type

(SELECT t1.proj_plan_id AS projPlanId, t1.business_id AS businessId, t1.plan_id AS planId, t2.full_borrow_money AS projMoney, t2.real_name AS userName, 0 AS isMast FROM tb_repayment_proj_plan t1 INNER JOIN tb_tuandai_project_info t2 ON t1.project_id = t2.project_id WHERE 1=1 AND IFNull(t2.org_issue_id,0) <> t2.project_id
AND t1.business_id = #{businessId}
AND t1.plan_id=#{planId}
ORDER BY t2.amount,t2.queryFullsuccessDate) UNION ALL (SELECT t1.proj_plan_id AS projPlanId, t1.business_id AS businessId, t1.plan_id AS planId, t2.full_borrow_money AS projMoney, t2.real_name AS userName, 0 AS isMast FROM tb_repayment_proj_plan t1 INNER JOIN tb_tuandai_project_info t2 ON t1.project_id = t2.project_id WHERE 1=1 AND IFNull(t2.org_issue_id,0)= t2.project_id
AND t1.business_id = #{businessId}
AND t1.plan_id=#{planId}
ORDER BY t2.amount,t2.queryFullsuccessDate)
