mapper.select:
SELECT
FROM (SELECT t.*, MAX(t.`retry_count`) cnt FROM (SELECT * FROM tb_sys_api_call_failure_record ORDER BY ref_id, retry_count DESC) t WHERE t.`api_code` = #{apiCode,jdbcType=VARCHAR} AND t.`module_name` = #{moduleName, jdbcType=VARCHAR}
AND t.ref_id = #{refId, jdbcType=VARCHAR}
GROUP BY t.`ref_id`) tab WHERE tab.cnt = retry_count AND tab.cnt >= 5
