SELECT uRole.user_id AS UserId FROM tb_sys_user_role uRole LEFT JOIN tb_sys_user_area uArea on uRole.user_id=uArea.user_id WHERE 1=1
and uRole.role_code = #{roleCode}
and uArea.org_code IN
#{orgCode}
GROUP By UserId

