SELECT bb.`business_id` businessId, -- 业务编号 ( CASE WHEN bb.`business_type` IN (1, 9) THEN 2 WHEN bb.`business_type` IN (2, 11) THEN 1 ELSE NULL END ) businessType, -- 业务类型 bb.`district_name` districtName, -- 片区 bb.`company_name` companyName, -- 分公司 bb.`customer_name` customerName -- 主借款人姓名 FROM `tb_basic_business` bb WHERE
bb.business_type IN (2, 11)
bb.business_type IN (1, 9)
bb.`business_type` IN (1, 2, 9, 11)
AND bb.`business_id` = #{businessId,jdbcType=VARCHAR}
AND bb.`district_name` = #{districtName,jdbcType=VARCHAR}
AND bb.`company_name` = #{companyName,jdbcType=VARCHAR}
AND bb.`customer_name` = #{customerName,jdbcType=VARCHAR}
LIMIT ${(page-1) * limit}, ${limit}

SELECT COUNT(DISTINCT bb.business_id) FROM `tb_basic_business` bb LEFT JOIN `tb_basic_repayment_type` brt ON bb.`repayment_type_id` = brt.`repayment_type_id` LEFT JOIN `tb_department_bank` db ON bb.`company_id` = db.`dept_id` LEFT JOIN `tb_car_basic` cb ON bb.`business_id` = cb.`business_id` LEFT JOIN `tb_fsd_house` fh ON bb.`business_id` = fh.`business_id` WHERE
bb.business_type IN (2, 11)
bb.business_type IN (1, 9)
bb.`business_type` IN (1, 2, 9, 11)
AND bb.`business_id` = #{businessId,jdbcType=VARCHAR}
AND bb.`district_name` = #{districtName,jdbcType=VARCHAR}
AND bb.`company_name` = #{companyName,jdbcType=VARCHAR}
