mapper.select:
select car.business_id, bus.district_name, bus.company_name, bus.customer_name, pl.borrow_money,
(select ifnull(sum(ifnull(detail.fact_amount,0)),0) from tb_repayment_biz_plan_list li join tb_repayment_biz_plan_list_detail detail on li.plan_list_id=detail.plan_list_id where li.current_status='已还款' and li.plan_id=pl.plan_id) repaid_amount, car.license_plate_number, car.model, det.evaluation_amount, date_format(det.create_time,'%Y-%m-%d') evaluation_date, date_format(drag.drag_date,'%Y-%m-%d') trailer_date, drag.id drag_id, case when car.status='01' then '待处置' when car.status='02' then '拍卖中' when car.status='03' then '已拍卖' when car.status='04' then '已结清' when car.status='05' then '已转公车' when car.status='06' then '已移交法务' when car.status='07' then '已撤销' when car.status='08' then '转公车申请中' when car.status='09' then '拍卖审核中' when car.status='10' then '转公车审核中' when car.status='11' then '已归还' else '' end status from tb_car_basic car join tb_basic_business bus on car.business_id=bus.business_id
join tb_repayment_biz_plan pl on pl.business_id=bus.business_id join tb_car_drag drag on drag.business_id=bus.business_id left join tb_car_detection det on IFNULL(car.last_detection_id,0)=det.id
not EXISTS (select * from tb_car_return_reg reg where drag.id=reg.drag_id)
and bus.business_id LIKE "%"#{businessId}"%"
and bus.district_id = #{areaId}
and bus.company_id = #{companyId}
and bus.customer_name LIKE "%"#{customerName}"%"
and car.license_plate_number LIKE "%"#{licensePlateNumber}"%"
and car.model LIKE "%"#{model}"%"
and car.status= #{status}
and drag.drag_date >=#{trailerStartDate}
and drag.drag_date <=#{trailerEndDate}
and bus.business_id in ( select business_id from tb_sys_user_permission where user_id = #{userId} and page_type = 2 )
order by bus.update_time desc limit ${(page-1) * limit}, #{limit,jdbcType=DECIMAL}

select count(1) from tb_car_basic car join tb_basic_business bus on car.business_id=bus.business_id
join tb_repayment_biz_plan pl on pl.business_id=bus.business_id left join tb_car_detection det on det.business_id=bus.business_id join tb_car_drag drag on drag.business_id=bus.business_id
and bus.business_id LIKE "%"#{businessId}"%"
and bus.district_id = #{areaId}
and bus.company_id = #{companyId}
and bus.customer_name LIKE "%"#{customerName}"%"
and car.license_plate_number LIKE "%"#{licensePlateNumber}"%"
and car.model LIKE "%"#{model}"%"
and car.status= #{status}
and drag.drag_date >=#{trailerStartDate}
and drag.drag_date <=#{trailerEndDate}
and bus.business_id in ( select business_id from tb_sys_user_permission where user_id = #{userId} and page_type = 2 )

select tca.auction_id auctionId, tca.business_id, tca.starting_price startPrice, tca.auction_start_time startPriceDate, tca.auction_end_time endPriceDate, tca.consultant contacts, tca.consultant_tel telephone, tca.payment_method paymentMethod, tca.auction_position auctionPosition, tca.remark remark, (case when tca.auction_start_time<=DATE_FORMAT(now(),'%Y-%m-%d') then tca.bidder_count else 0 end) bidderCount, tcb.brand_name vehicleBrand , case when tcb.origin_place=0 then '国产' when tcb.origin_place=1 then '进口' else '' end carproduction , tcb.color carColour , tcb.model carModel , tcb.license_location vehicleTerritory, concat(tcb.displacement,tcb.displacement_unit) displacement , tcb.engine_model engineNumber , tcb.vin frameNumber , det.user_nature useProperty , det.insurance_expiration_date insuranceDate , det.annual_verification_expiration_date inspectionDate , det.odometer mileage , det.vehicle_license_registration_date registerDate , case when det.is_mortgage=false then '未抵押' when det.is_mortgage=true then '抵押' else '' end mortgageState , det.traffic_violation_situation illegal , det.tool_with_car tools , det.transaction_mode transactionMode , det.car_position position , det.related_docs file , det.remark remarks, (select trans_price from tb_car_auction_reg a where a.auction_id=tca.auction_id and a.is_auction_success=true) tranPrice from tb_car_auction tca join tb_car_basic tcb on tca.business_id=tcb.business_id join tb_car_detection det on tcb.last_detection_id=det.id join tb_process tp on tcb.business_id=tp.business_id and tp.process_result=1 join tb_process_type tpt on tpt.type_id=tp.process_typeid and tpt.type_code='carAuctionAply'
tca.status in ('04','05')
and tca.status =#{type}
and (tcb.brand_name LIKE "%"#{vehicleBrand}"%" or tcb.model LIKE "%"#{carModel}"%")
and tca.auction_id =#{auctionId}
and tca.auction_end_time>=DATE_FORMAT(now(),'%Y-%m-%d')
order BY tca.auction_end_time DESC limit ${(page-1) * limit}, #{limit}

select tcab.bidder_name bidderName, tcab.bidder_cert_id bidderCertId, tcab.bidder_tel bidderTel, tcab.trans_account_name transAccountName, tcab.trans_account_num transAccountNum, tcab.trans_account_num transAccountNum, tcar.reg_id regId, tcar.business_id businessId, tcar.is_pay_deposit isPayDeposit, case when tcar.is_pay_deposit=0 then '否' when tcar.is_pay_deposit=1 then '是' else '' end payDepositStr,
tcar.offer_amount offerAmount, tcar.is_auction_success isAuctionSuccess, case when tcar.is_auction_success=0 then '否' when tcar.is_auction_success=1 then '是' else '' end auctionSuccessStr, tcar.trans_price transPrice, tcar.auction_id auctionId, tcar.update_user updateUser, tcar.update_time updateTime from tb_car_auction_bidder tcab join tb_car_auction_reg tcar on tcar.reg_tel=tcab.bidder_tel join tb_car_auction tca on tca.auction_id=tcar.auction_id
and tcar.auction_id= #{priceID}
and tcar.reg_tel= #{telephone}
order BY tcar.update_time limit ${(page-1) * limit}, #{limit}

select count(*) from tb_car_auction_bidder tcab join tb_car_auction_reg tcar on tcar.reg_cert_id=tcab.bidder_cert_id join tb_car_auction tca on tca.auction_id=tcar.auction_id
and tcar.auction_id= #{priceID}
and tcar.reg_tel= #{telephone}

select tca.auction_id auctionId, tca.business_id, tca.starting_price startPrice, tca.auction_start_time startPriceDate, tca.auction_end_time etartPriceDate, tca.consultant contacts, tca.consultant_tel telephone, case when tca.pay_way='01' then '竞买成功后，尾款线下支付' when tca.pay_way='02' then '竞买成功后，尾款线上支付' else '' end paymentMethod, case tca.car_position auctionPosition, tca.remark remark,
tcb.brand_name vehicleBrand , case when tcb.origin_place=0 then '国产' when tcb.origin_place=1 then '进口' else '' end carproduction , tcb.color carColour , tcb.model carModel , tcb.license_location vehicleTerritory, concat(tcb.displacement,tcb.displacement_unit) displacement , tcb.engine_model engineNumber , tcb.vin frameNumber , det.user_nature useProperty , det.insurance_expiration_date insuranceDate , det.annual_verification_expiration_date inspectionDate , det.odometer mileage , det.vehicle_license_registration_date registerDate , case when det.is_mortgage=false then '未抵押' when det.is_mortgage=true then '抵押' else '' end mortgageState , det.traffic_violation_situation illegal , det.tool_with_car tools , det.transaction_mode transactionMode , det.car_position position , det.related_docs file , det.remark remarks from tb_car_auction tca join tb_car_basic tcb on tca.business_id=tcb.business_id join tb_car_detection det on tcb.last_detection_id=det.id join tb_process tp on tcb.business_id=tp.business_id and tp.process_result=1 join tb_process_type tpt on tpt.type_id=tp.process_typeid and tpt.type_code='carAuctionAply'
tca.status in ('04','05')
and tca.status =#{type}
and tca.auction_id =#{auctionId}
and (tcb.brand_name LIKE "%"#{vehicleBrand}"%" or tcb.model LIKE "%"#{carModel}"%")
order BY tca.auction_end_time asc limit ${(page-1) * limit}, #{limit}

select count(*)
from tb_car_auction tca join tb_car_basic tcb on tca.business_id=tcb.business_id join tb_car_detection det on tcb.last_detection_id=det.id join tb_process tp on tcb.business_id=tp.business_id and tp.process_result=1 join tb_process_type tpt on tpt.type_id=tp.process_typeid and tpt.type_code='carAuctionAply' join tb_car_auction_reg tcar on tca.auction_id=tcar.auction_id join tb_car_auction_bidder tcab on tcar.reg_tel=tcab.bidder_tel
and tcar.is_auction_success= ${1}
and tcar.auction_id= #{priceID}
and tcar.reg_tel= #{telephone}
and (tcb.brand_name LIKE "%"#{vehicleBrand}"%" or tcb.model LIKE "%"#{carModel}"%")
