mapper.select:
select business.business_id AS businessId, pList.period as periods, pList.after_id as afterId,
business.district_id as districtId, business.district_name AS districtAreaName, -- districtAreaName business.company_id as companyId, business.company_name as companyName, -- company.area_name AS companyName, business.operator_id AS operatorId, business.operator_name AS operatorName, business.customer_id AS customerId, business.customer_name AS customerName, business.business_type AS businessTypeId, b_type.business_type_name AS businessTypeName, business.borrow_money AS borrowMoney, pList.total_borrow_amount as totalBorrowAmount, pList.overdue_days as delayDays, pList.due_date as dueDate, pList.fact_repay_date AS repaymentDate, col_status.phone_staff AS phoneStaffId, -- phoneUser.user_name AS phoneStaffName,
col_status.visit_staff AS visitStaffId, -- visitUser.user_name AS visitStaffName,
pList.plan_list_id AS crpId, (case when plan.plan_status = 10 or plan.plan_status = 20 OR plan.plan_status =30 then '已结清' else pList.current_status end)as statusName, -- pList.current_status as statusName, col_status.collection_status as colStatus, -- sys_param.param_name as afterColStatusName
plan.borrow_limit as borrowLimit , business.repayment_type_id as repaymentTypeId , changeLog.class_name className
from tb_repayment_biz_plan_list as pList LEFT JOIN tb_repayment_biz_plan as plan ON pList.plan_id = plan.plan_id LEFT JOIN tb_basic_business as business ON plan.original_business_id = business.business_id LEFT JOIN tb_basic_company as company ON business.company_id = company.area_id LEFT JOIN tb_basic_business_type as b_type ON business.business_type = b_type.business_type_id LEFT JOIN tb_collection_status as col_status ON pList.plan_list_id = col_status.crp_id JOIN tb_sys_user_permission as permission ON permission.business_id = pList.orig_business_id left JOIN tb_five_level_classify_business_change_log as changeLog ON changeLog.orig_business_id = business.business_id and changeLog.valid_status = '1' -- LEFT JOIN tb_sys_user as phoneUser ON col_status.phone_staff = phoneUser.user_id -- LEFT JOIN tb_sys_user as visitUser ON col_status.visit_staff = visitUser.user_id -- LEFT JOIN tb_sys_parameter as sys_param ON col_status.collection_status = sys_param.param_value
-- LEFT JOIN tb_sys_parameter as sys_param ON col_status.collection_status = sys_param.param_value
1=1 and IFNULL(pList.repay_flag,0) != 6 -- sys_param.param_type = 2
and pList.due_date >= #{showRepayDateBegin,jdbcType=TIMESTAMP}
and pList.due_date <= #{showRepayDateEnd,jdbcType=TIMESTAMP}
and pList.fact_repay_date >= #{realRepayDateBegin,jdbcType=TIMESTAMP}
and pList.fact_repay_date <= #{realRepayDateEnd,jdbcType=TIMESTAMP}
and pList.overdue_days >= #{delayDaysBegin}
and pList.overdue_days < #{delayDaysEnd}
and business.operator_name LIKE '%${operatorName}%'
and business.business_id LIKE '%${businessId}%'
and business.business_type = #{businessType}
and col_status.collection_status = #{businessStatus}
and changeLog.class_name = #{businessClassName}
and pList.current_status = #{repayStatus}
and (plan.plan_status = 10 or plan.plan_status = 20 OR plan.plan_status =30)
and col_status.phone_staff IN
#{userId}
and col_status.visit_staff IN
#{userId}
and business.customer_id IN
#{customerId}
and business.company_id IN
#{commId}
and business.customer_name LIKE '%${customerName}%'
and pList.plan_list_id IN
#{crpId}
and permission.user_id = #{userId}
and business.business_id IN
#{bizId}
and plan.is_defer != 1 and pList.period = 1
and business.repayment_type_id = 2 and plan.borrow_limit = pList.period
and business.repayment_type_id = 5 and plan.borrow_limit = pList.period
order BY pList.due_date

select business.business_id AS businessId, pList.plan_list_id as crpId, pList.period as periods, pList.after_id as afterId, business.district_id as districtId, business.district_name AS districtAreaName, business.company_id as companyId, business.company_name as companyName, business.operator_id AS operatorId, business.operator_name AS operatorName, business.customer_id AS customerId, business.customer_name AS customerName, business.business_type AS businessTypeId, b_type.business_type_name AS businessTypeName, business.borrow_money AS borrowMoney, pList.total_borrow_amount as totalBorrowAmount, pList.overdue_days as delayDays, pList.due_date as dueDate, pList.fact_repay_date AS repaymentDate,
business.repayment_type_id as repaymentTypeId , (case when plan.plan_status = 10 or plan.plan_status = 20 OR plan.plan_status =30 then '已结清' else pList.current_status end)as statusName
from tb_repayment_biz_plan_list as pList JOIN tb_repayment_biz_plan as plan ON pList.plan_id = plan.plan_id JOIN tb_basic_business as business ON plan.original_business_id = business.business_id left JOIN tb_basic_company as company ON business.company_id = company.area_id left JOIN tb_basic_business_type as b_type ON business.business_type = b_type.business_type_id -- JOIN tb_sys_user_permission as permission ON permission.business_id = business.business_id -- LEFT JOIN tb_sys_user as phoneUser ON col_status.phone_staff = phoneUser.user_id -- LEFT JOIN tb_sys_user as visitUser ON col_status.visit_staff = visitUser.user_id -- LEFT JOIN tb_sys_parameter as sys_param ON col_status.collection_status = sys_param.param_value
-- LEFT JOIN tb_sys_parameter as sys_param ON col_status.collection_status = sys_param.param_value
1=1 and IFNULL(pList.repay_flag,0) != 6 and (plan.plan_status=0 or plan.plan_status=50)
and ((business.operator_name LIKE '%${keyName}%') or (business.business_id LIKE '%${keyName}%') or (business.customer_name LIKE '%${keyName}%') )
and pList.due_date >= #{showRepayDateBegin,jdbcType=TIMESTAMP}
and pList.due_date <= #{showRepayDateEnd,jdbcType=TIMESTAMP}
and pList.fact_repay_date >= #{realRepayDateBegin,jdbcType=TIMESTAMP}
and pList.fact_repay_date <= #{realRepayDateEnd,jdbcType=TIMESTAMP}
and pList.overdue_days >= #{delayDaysBegin}
and pList.overdue_days < #{delayDaysEnd}
and business.operator_name LIKE '%${operatorName}%'
and business.business_id LIKE '%${businessId}%'
and business.business_type = #{businessType}
and business.customer_id IN
#{customerId}
and business.company_id IN
#{commId}
and business.customer_name LIKE '%${customerName}%'
order BY pList.due_date desc
