mapper.select:
select s.type_step_id as typeStepId,s.type_id as typeId,s.step_name as stepName,t.type_name as typeName,
u.user_name as approveUserName, s.approve_user_type as approveUserType, s.approve_user_id_select_sql as approveUserIdSelectSql,s.is_can_edit as isCanEdit,
s.step,s.next_step as nextStep ,s.step_type as stepType,s.approve_user_role as approveUserRole
from
tb_process_type_step s left join tb_process_type t on t.type_id=s.type_id
left join tb_sys_user u on u.user_id=s.approve_user_id
1=1
and s.type_id=#{typeId}
and s.step_name = #{stepName}
and s.type_step_id = #{typeStepId}
